import React, { useState } from 'react';
import CartView from '../components/CartView';
import { Text, TextInput, StyleSheet, SafeAreaView, ScrollView, View, TouchableOpacity } from 'react-native';
import COLORS from '../components/Colors';


function CartScreen({ userdata }) {
    const [countcarteditem, setCountCartedItem] = useState(0);
    //carted item count is performed here 

    const handledelete = () => {
        console.log("delete the selected item")

    }
    return (
        <SafeAreaView style={styles.maincont} >

            <TextInput style={styles.search}
                placeholder='search' />

            <Text style={styles.countitem} >Your Carted Items - {countcarteditem}</Text>

            <ScrollView style={styles.productcont}>
                <CartView userdata={userdata} setCountCartedItem={setCountCartedItem} />
                {/* <ProductView setproductdata={setproductdata} /> */}

            </ScrollView>
            <View style={
                {
                    flex: -1,
                    height: 55,
                    backgroundColor: "#fff",
                    borderBottomLeftRadius: 25,
                    borderTopRightRadius: 40,
                    bottom: 5,

                    justifyContent: "space-around",
                    alignItems: "center",
                    flexDirection: "row",
                    borderColor: "green",
                    borderWidth: 2
                }}>
                <TouchableOpacity
                    onPress={() => handledelete()}
                    style={styles.bottombuttons}>
                    <Text>DELETE</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.bottombuttons}>
                    <Text>PLACE ORDER</Text>
                </TouchableOpacity>

            </View>




        </SafeAreaView >






    );
}
const styles = StyleSheet.create({
    maincont: {
        flex: 1,
        backgroundColor: "#6a73cc",


    },

    search: {

        height: 50,
        color: 'black',
        width: "100%",
        textAlign: 'center',
        fontSize: 20,
        backgroundColor: 'white',
        borderRadius: 10,
        borderStartColor: "red"



    },
    productcont: {
        top: 10,
        flex: 1,
        marginHorizontal: 7,
        backgroundColor: 'white',
        overflow: "scroll"
    },
    countitem: {

        textAlign: 'center',
        fontSize: 20,
        fontWeight: 'bold'



    },


    bottombuttons: {
        height: 45,
        width: "35%",
        backgroundColor: "#ba3761",
        // borderRadius: 20,
        borderTopLeftRadius: 25,
        borderTopRightRadius: 25,
        justifyContent: "center",
        alignItems: "center"
    }
})

export default CartScreen;
